import React from 'react'
import { Consumer } from '../../../context';
import Detailes from '../Detailes';
import { v4 as uuid } from "uuid"
function SearchText() {
  return (
    <Consumer>
      {(value) => {
        const { restarent, handler } = value;
        const serchText = value;

        let filterList;
        if (serchText === "")
          filterList = [];
        else {
          filterList = restarent.filter((item) => {
            return item.name.toLowerCase().includes(serchText.toLowerCase());
          });
          handler("addSuggesstion", filterList);
          handler("addSearchText", serchText)
        }
      }}
    </Consumer>
  )

}

export default SearchText
