import axios from 'axios';
import React, { Component } from 'react'
import FilterCard from './FilterCard';
import { Consumer } from '../../context';
import "../Styles/filter.css"


class Filter extends Component {
  state = {
    city: "",
    restarents: []
  }
  onchange = (event) => {
    this.setState({ [event.target.city]: event.target.value })
    axios({
      method: "GET",
      url: `https://assignment7ofedureka.herokuapp.com/restarent/getRestaurantByCity?city=${event.target.value}`,
      handler: { "Content-type": "application/json" }
    }).then((responce) => this.setState({ restarents: responce.data }))
  }
  handSelct = (handler, event) => {
    event.preventDefault();

    const newCity = {
      city: this.state.city
    }
    handler("addCity", newCity)
  }
  render() {

    return (
      <Consumer>
        {(value) => {
          const { restarent } = value;
          const { handler } = value;
          return (
            <div className='container'>
              <div className="mainTitle">
                Breakfast Places in {restarent.city}
              </div>
              <div className="row">
                <div className="firstcol col-md-4 col-sm-12">
                  <div className="Filter">Filter</div>
                  <div className="subheading">
                    Select Location
                  </div>
                  <select name="" id="" className="select" onChange={this.handSelct.bind(this, handler)}>
                    <option>Select Loocation</option>
                    {
                      restarent.map((item) => {
                        return <option key={item._id} onChange={this.onchange} value={item.city}>{`${item.name},${item.city}`}</option>
                      })
                    }
                  </select>
                  <div>
                    {/* <form action="" onSubmit={this.onSubmit.bind(this, handler)}> */}
                    <form>
                      <div className="head">Cusine</div>
                      {
                        restarent.map((item) => {
                          return (<div>
                            <input className='element' type="checkbox" name={item.cuisine} />
                            <label className='element' htmlFor={item.cuisine}>{item.cuisine}</label>
                          </div>)
                        })
                      }
                      <div className='head'>Cost For Two</div>
                      <input className='inputelement' type="radio" name="cost" id="lessthan500" />
                      <label className='element' htmlFor="lessthan500">less than ₹500</label><br />
                      <input className='inputelement' type="radio" name="cost" id="500to1000" />
                      <label className='element' htmlFor="500to1000"> ₹500 to ₹1000</label><br />
                      <input className='inputelement' type="radio" name="cost" id="1000to1500" />
                      <label className='element' htmlFor="1000to1500">₹1000 to ₹1500</label><br />
                      <input className='inputelement' type="radio" name="cost" id="1500to2000" />
                      <label className='element' htmlFor="1500to2000"> ₹1500 to ₹2000</label><br />
                      <input className='inputelement' type="radio" name="cost" id="2000" />
                      <label className='element' htmlFor="2000"> ₹2000+</label><br />
                      <div className='head'>Sort</div>
                      <input className='inputelement' type="radio" name="sort" id="lowtohigh" />
                      <label className='element' htmlFor="lowtohigh">Price low to high</label><br />
                      <input className='inputelement' type="radio" name="sort" id="hightolow" />
                      <label className='element' htmlFor="hightolow"> Price high to low</label><br />
                      <button className='onsubmit' type="submit">Apply</button>
                    </form>
                  </div>
                </div>
                <div className="col-md-6 col-sm-12">
                  <FilterCard restarents={this.state.restarents} />
                </div>
              </div>
            </div>
          )
        }}
      </Consumer>

    )
  }
}

export default Filter
