import axios from 'axios';
import React, { Component } from 'react'
import FilterCard from './FilterCard';
import { Consumer } from '../../context';
import "../Styles/filter.css"
import ReactPaginate from 'react-paginate';

class Filter extends Component {
  state = {
    city: "Mumbai",
    restarents: [
      {
        _id: "1", name: "Domino's", description: "A good family restarent", address: "5-12/a", location: "HariNargar", image: "../assets/Images/breakfast.jpg", min_price: "550", contact: "+91780000", cuisine: "North Indian", locality: "near golhanuman hemple", city: "Mumbai", mealtype: "dinner"
      }
    ],
    restarent: []
  }
  onchange = (event) => {
    const locationName = event.target.value;
    axios({
      method: "GET",
      url: `https://assignment7ofedureka.herokuapp.com/restarent/getRestaurantByCity?city=${locationName}`,
      handler: { "Content-type": "application/json" }
    }).then((responce) => { this.setState({ restarents: responce.data }) }).catch((e) => { console.log(e) })
    this.setState({ city: locationName });
  }
  handSelct = (handler, event) => {
    event.preventDefault();

    const newCity = {
      city: this.state.city
    }
    handler("addCity", newCity)
  }
  renderSuggesion = () => {
    const { restarents } = this.state;
    return (
      <div className='m-2 card py-4'>
        <div className="card-body">
          <img className='card-img float-left image-fluid rounded-25' src={restarents.image} alt="" />
          <div className="">
            <h1>{restarents.name}</h1>
          </div>
        </div>
      </div>
    )
  }
  handlePagination = (data) => {
    const { restarents } = this.state;
    this.setState({ count: data.selected })
    this.setState({ restarent: restarents.slice(data.selected, data.selected + 1) })

  }
  render() {

    return (
      <Consumer>
        {(value) => {
          const { restarent } = this.state;
          return (
            <div className='container'>
              <div className="mainTitle">
                <div className="mt-4">
                  Breakfast Places in  {this.state.city}

                </div>
              </div>
              <div className="row">
                <div className="firstcol col-lg-3 col-md-4 col-sm-12 shadow p-4 m-3">
                  <div className="Filter">Filter</div>
                  <div className="head">
                    Select Location
                  </div>
                  <select name="" id="" className="select py-2" onChange={this.onchange}>
                    <option>Select</option>
                    {
                      restarent.map((item) => {
                        return <option className='select' key={item._id} value={item.city}>{`${item.name},${item.city}`}</option>
                      })
                    }
                  </select>
                  <div>
                    <form action="" onSubmit={this.onSubmit}>
                      <div className="head mt-2">Cusine</div>
                      <input className='element' type="checkbox" name="north" />
                      <label className='element' htmlFor="north">&nbsp; North Indian</label><br />
                      <input className='element' type="checkbox" name="South" />
                      <label className='element' htmlFor="South">&nbsp;South Indian</label><br />
                      <input className='element' type="checkbox" name="chines" />
                      <label className='element' htmlFor="chines">&nbsp;Chines</label><br />
                      <input className='element' type="checkbox" name="fast" />
                      <label className='element' htmlFor="fast">&nbsp;Fast Food</label><br />
                      <input className='element' type="checkbox" name="street" />
                      <label className='element' htmlFor="street">&nbsp;Street Food</label><br />
                      {/** Cost */}
                      <div className='head'>Cost For Two</div>
                      <input className='inputelement' value={100} type="radio" name="cost" id="lessthan500" />
                      <label className='element' htmlFor="lessthan500">&nbsp;less than ₹500</label><br />
                      <input className='inputelement' value={500} type="radio" name="cost" id="500to1000" />
                      <label className='element' htmlFor="500to1000">&nbsp; ₹500 to ₹1000</label><br />
                      <input className='inputelement' value={1000} type="radio" name="cost" id="1000to1500" />
                      <label className='element' htmlFor="1000to1500">&nbsp;₹1000 to ₹1500</label><br />
                      <input className='inputelement' value={1500} type="radio" name="cost" id="1500to2000" />
                      <label className='element' htmlFor="1500to2000">&nbsp; ₹1500 to ₹2000</label><br />
                      <input className='inputelement' value={2000} type="radio" name="cost" id="2000" />
                      <label className='element' htmlFor="2000"> &nbsp;₹2000+</label><br />
                      <div className='head'>Sort</div>
                      <input className='inputelement' value={"lowtohigh"} type="radio" name="sort" id="lowtohigh" />
                      <label className='element' htmlFor="lowtohigh">&nbsp;Price low to high</label><br />
                      <input className='inputelement' value={"hightolow"} type="radio" name="sort" id="hightolow" />
                      <label className='element' htmlFor="hightolow"> &nbsp;Price high to low</label><br />
                      <button className='onsubmit btn btn-outline-primary ml-4 px-5 shadow  my-2' type="submit">Apply</button>
                    </form>
                  </div>
                </div>
                <div className="col-md-7 col-sm-12 mt-2">
                  {restarent && restarent.map((item) => {
                    return <FilterCard item={item} />
                  })
                  }
                  <ReactPaginate
                    previousLabel={"<<"}
                    nextLabel={">>"}
                    containerClassName={"pagination justify-content-center pagecontainer"}
                    previousClassName={"page-item"}
                    marginPagesDisplayed={2}
                    breakLabel={"..."}
                    pageClassName={"page-item rounded"}
                    pageLinkClassName={"page-link mx-2 rounded"}
                    previousLinkClassName={"page-link"}
                    activeClassName={""}
                    activeLinkClassName={""}
                    nextClassName={"page-item rounded"}
                    nextLinkClassName={"page-link"}
                    pageCount={5}
                    pageRangeDisplayed={2}
                    onPageChange={this.handlePagination}
                  />
                </div>

              </div>
            </div>
          )
        }}
      </Consumer>

    )
  }
}

export default Filter
