import axios from "axios";
import React, { Component } from "react";
import "../styles/wallpaper.css";
import { FiSearch } from "@react-icons/all-files/fi/FiSearch";
import { Link } from "react-router-dom";

import { v4 as uuid } from 'uuid'
import { Consumer } from "../../context";
import Suggesstions from "./Wallpaper/Suggesstions";
import SearchText from "./Wallpaper/SearchText";
class Wallpaper extends Component {
  state = {
    restarent: [],
    Suggestions: [],
    serchText: "",
  }

  //function that call on change of the selector
  handCity = (event, handler) => {
    let location = event.target.value;
    handler("addLocation", location)
  }

  //function tha call on seach
  handlingSearch = () => {
    <SearchText />
  };


  render() {
    return (
      <Consumer>
        {(values) => {
          const { locationValues, handler, restarent } = values;
          const serchText = values;
          return (
            <div className="container-fluid main text-center">
              <h1 className="mainicon">e!</h1>
              <h5 className="title">Find the best restaurants and caf√©s</h5>
              <div className="row mx-5 px-5 selcetor-row text-center">
                <div className="col-md-6 col-sm-12">
                  <select
                    name=""
                    id=""
                    className="Selector mt-1 rounded-0"
                    onChange={this.handCity.bind(this, handler)}
                  >
                    <option key="1" className="text-secondary" value="">
                      Please select value
                    </option>
                    {locationValues &&
                      locationValues.map((item) => {
                        return (
                          <option
                            value={item.city}
                          >{`${item.name},${item.city}`}</option>
                        );
                      })}
                  </select>
                </div>
                <div className="col-md-6 col-sm-12 serchcol mt-1">
                  <FiSearch className="serch-icon" />
                  <input
                    type="text"
                    name=""
                    id=""
                    placeholder="enter restarent name"
                    className="px-5 py-2 rounded-0"
                    // onChange={<SearchText restarent={restarent} serchText={serchText} />}
                    onChange={this.handlingSearch}
                  />
                  <Suggesstions restarent={restarent} serchText={serchText} />
                </div>
              </div>
            </div>
          );
        }
        }
      </Consumer>
    )
  }
}

export default Wallpaper;
