import React, { Component } from 'react'
import { Consumer } from '../../context';



class Filter extends Component {
  handSelct = (event, handler) => {
    let locationName = event.target.value;
    handler("addLocation", locationName)
  }
  render() {

    return (
      <Consumer>
        {(value) => {
          const { restarentData } = props;
          const { handler } = value;
          return (
            <div className='container'>
              <div className="mainTitle">
                Breakfast Places in {restarentData.city}
              </div>
              <div className="row">
                <div className="col-md-4 col-sm-12">
                  <div className="Filter">Filter</div>
                  <div className="subheading">
                    Select Location
                  </div>
                  <select name="" id="" className="select" onChange={this.handSelct.bind(this, handler)}>
                    {
                      restarentData.map((item) => {
                        return <option value={item.city}>{item.city}</option>
                      })
                    }
                  </select>
                </div>
                <div className="col-md-6 col-sm-12">

                </div>
              </div>
            </div>
          )
        }}
      </Consumer>

    )
  }
}

export default Filter
