import React, { Component } from 'react'
import { Consumer } from '../../context';
import { v4 as uuid } from "uuid"


class Filter extends Component {
  state = {
    city: ""
  }
  onchange = (event) => {
    this.setState({ [event.target.city]: event.target.value })
  }
  handSelct = (handler, event) => {
    event.preventDefault();

    const newCity = {
      city: this.state.city
    }
    handler("addCity", newCity)
  }
  render() {

    return (
      <Consumer>
        {(value) => {
          const { restarent } = value;
          const { handler } = value;

          return (
            <div className='container'>
              <div className="mainTitle">
                Breakfast Places in {restarent.city}
              </div>
              <div className="row">
                <div className="col-md-4 col-sm-12">
                  <div className="Filter">Filter</div>
                  <div className="subheading">
                    Select Location
                  </div>
                  <select name="" id="" className="select" onChange={this.handSelct.bind(this, handler)}>
                    <option>Select Loocation</option>
                    {
                      restarent.map((item) => {
                        return <option onChange={this.onchange} value={item.city}>{`${item.name},${item.city}`}</option>
                      })
                    }
                  </select>
                </div>
                <div className="col-md-6 col-sm-12">

                </div>
              </div>
            </div>
          )
        }}
      </Consumer>

    )
  }
}

export default Filter
